import React, { Component } from 'react';
import { Menu, Input, Label } from 'semantic-ui-react';

class MainMenu extends Component {
  constructor() {
    super();
    this.state = {
      activeItem: 'schedule'
    };
  }
  handleItemClick = (e, { name }) => this.setState({ activeItem: name });
  render = () => {
    const { activeItem } = this.state;
    return (
      <Menu pointing vertical className="mainMenu">
        <Menu.Item name="schedule" active={activeItem === 'schedule'} onClick={this.handleItemClick}>
          Schedule
        </Menu.Item>

        <Menu.Item name="staff" active={activeItem === 'staff'} onClick={this.handleItemClick}>
          <Label>51</Label>
          Staff
        </Menu.Item>
        <Menu.Item>
          <Input icon="search" placeholder="Search mail..." />
        </Menu.Item>
      </Menu>
    );
  };
}
export default MainMenu;
