import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Table, Button, Modal, Form } from 'semantic-ui-react';

class U_Staff extends Component {
  submit = () => {
    let newid = () => {
      this.props.staff.sort();
      return this.props.staff[this.props.staff.length - 1] + 10;
    };
    let newEmploye = {
      id: this.props.staff
    };
    this.props.dispatch({
      type: 'add-employees',
      employees: staffData
    });
  };
  render() {
    return (
      <div className="staff">
        <Modal trigger={<Button>Create</Button>}>
          <Modal.Header>Create a new employe</Modal.Header>
          <Modal.Content>
            <Form onSubmit={this.submit}>
              <Form.Field>
                <label>First Name</label>
                <input placeholder="First Name" />
              </Form.Field>
              <Form.Field>
                <label>Last Name</label>
                <input placeholder="Last Name" />
              </Form.Field>
              <Form.Field>
                <label>Group</label>
                <input placeholder="Group" />
              </Form.Field>
              <Button type="submit">Submit</Button>
            </Form>
          </Modal.Content>
        </Modal>

        <Table celled>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Group</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            {this.props.staff.map((employe, index) => {
              return (
                <Table.Row key={index}>
                  <Table.Cell>{employe.firstName + ' ' + employe.lastName}</Table.Cell>
                  <Table.Cell>{employe.group}</Table.Cell>
                </Table.Row>
              );
            })}
          </Table.Body>
        </Table>
      </div>
    );
  }
}
let mapStateToProps = state => {
  return {
    staff: state.employees
  };
};
let Staff = connect(mapStateToProps)(U_Staff);
export default Staff;
