import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Table, Button, Modal } from 'semantic-ui-react';
import CreateEmploye from './CreateEmploye';
class U_Staff extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false
    };
  }
  openModal = () => {
    this.setState({ open: true });
  };
  render() {
    // let { open } = this.props.open;
    console.log('open', this.props.open);
    console.log('staff', this.props.staff);
    return (
      <div className="staff">
        <Modal trigger={<Button onClick={openModal}>Create</Button>} open={this.props.open}>
          <Modal.Header>Create a new employe</Modal.Header>
          <Modal.Content>
            <CreateEmploye />
          </Modal.Content>
        </Modal>

        <Table celled>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Group</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            {this.props.staff.map((employe, index) => {
              return (
                <Table.Row key={index}>
                  <Table.Cell>{employe.firstName + ' ' + employe.lastName}</Table.Cell>
                  <Table.Cell>{employe.group}</Table.Cell>
                </Table.Row>
              );
            })}
          </Table.Body>
        </Table>
      </div>
    );
  }
}
let mapStateToProps = state => {
  return {
    staff: state.employees,
    open: state.open
  };
};
let Staff = connect(mapStateToProps)(U_Staff);
export default Staff;
