import React, { Component } from 'react';
import { Menu, Input, Label } from 'semantic-ui-react';
import { withRouter } from 'react-router-dom';
import fetch from './apiMocked.js';
// global.fetch = fetch;

class MainMenu extends Component {
  constructor() {
    super();
    this.state = {
      //menu by default
      activeItem: 'schedule',
      countEmployees: 0
    };
  }
  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name });
    //for navigation
    this.props.history.push('/' + name);
  };
  componentDidMount() {
    let apiUrl = 'test';
    return fetch(`${apiUrl}/tasks`)
      .then(staffResponse => staffResponse.json())
      .then(staffData => {
        this.setState({
          countEmployees: staffData.length
        });
      })
      .catch(error => {
        console.log(error);
      });
  }
  render = () => {
    const { activeItem } = this.state;
    return (
      <Menu pointing vertical className="mainMenu">
        <Menu.Item name="schedule" active={activeItem === 'schedule'} className="firstMenu" onClick={this.handleItemClick}>
          Schedule
        </Menu.Item>

        <Menu.Item name="staff" active={activeItem === 'staff'} onClick={this.handleItemClick}>
          <Label>{this.state.countEmployees}</Label>
          Staff
        </Menu.Item>
        <Menu.Item>
          <Input icon="search" placeholder="Search staff..." />
        </Menu.Item>
      </Menu>
    );
  };
}
export default withRouter(MainMenu);
